import{_ as e,c as a,o as t,a as r}from"./app.c931d240.js";const f='{"title":"Studying from the Inside(\u4ECE\u5185\u90E8\u5B66\u4E60)","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4ECE\u5916\u9762\u5B66\u4E60","slug":"\u4ECE\u5916\u9762\u5B66\u4E60"},{"level":2,"title":"\u4ECE\u5185\u90E8\u5B66\u4E60","slug":"\u4ECE\u5185\u90E8\u5B66\u4E60"}],"relativePath":"guide/\u7B2C\u56DB\u7AE0-\u4ECE\u5185\u90E8\u5B66\u4E60.md","lastUpdated":1653894561000}',p={},n=r('<h1 id="studying-from-the-inside-\u4ECE\u5185\u90E8\u5B66\u4E60" tabindex="-1">Studying from the Inside(\u4ECE\u5185\u90E8\u5B66\u4E60) <a class="header-anchor" href="#studying-from-the-inside-\u4ECE\u5185\u90E8\u5B66\u4E60" aria-hidden="true">#</a></h1><h2 id="\u4ECE\u5916\u9762\u5B66\u4E60" tabindex="-1">\u4ECE\u5916\u9762\u5B66\u4E60 <a class="header-anchor" href="#\u4ECE\u5916\u9762\u5B66\u4E60" aria-hidden="true">#</a></h2><p>\u7814\u7A76\u6211\u4EEC\u7684 JavaScript \u4E16\u754C\u7684\u4E00\u79CD\u65B9\u6CD5\u662F<em>\u4ECE\u5916\u90E8\u7814\u7A76\u5B83</em>\u3002</p><p>\u6211\u4EEC\u53EF\u80FD\u4F1A\u4E13\u6CE8\u4E8E\u6A21\u62DF\u6211\u4EEC\u7684\u4E16\u754C\u2014\u2014\u4E00\u4E2A JavaScript \u5F15\u64CE\u2014\u2014\u662F\u5982\u4F55\u201C\u771F\u6B63\u201D\u5DE5\u4F5C\u7684\u3002\u4F8B\u5982\uFF0C\u6211\u4EEC\u53EF\u80FD\u4F1A\u4E86\u89E3\u5230\u8FD9\u4E2A\u6587\u672C\u5B57\u7B26\u4E32\u2014\u2014<em>\u6211\u4EEC\u4E16\u754C\u4E2D\u7684\u4E00\u4E2A\u503C</em>\u2014\u2014\u662F\u5B58\u50A8\u5728\u7845\u82AF\u7247\u4E2D\u7684\u5B57\u8282\u5E8F\u5217\u3002</p><p>\u8FD9\u79CD\u65B9\u6CD5\u5C06\u6211\u4EEC\u7684\u6CE8\u610F\u529B\u96C6\u4E2D\u5728\u4EBA\u548C\u8BA1\u7B97\u673A\u7684\u7269\u7406\u4E16\u754C\u4E0A\u3002\u6211\u4EEC\u7684\u65B9\u6CD5\u4E0D\u540C\u3002</p><p>\u6BD4\u5982\u6211\u4EEC\u53BB\u9605\u8BFBJS\u7684\u5F15\u64CE\u5982\u4F55\u6267\u884C\u7684\uFF0C\u8BA1\u7B97\u673A\u5982\u4F55\u5206\u914D\u5185\u5B58\u7684\u7B49\u7B49</p><h2 id="\u4ECE\u5185\u90E8\u5B66\u4E60" tabindex="-1">\u4ECE\u5185\u90E8\u5B66\u4E60 <a class="header-anchor" href="#\u4ECE\u5185\u90E8\u5B66\u4E60" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u5C06\u4ECE<em>\u5185\u90E8</em>\u7814\u7A76\u6211\u4EEC\u7684\u4E16\u754C\u3002\u5C06\u81EA\u5DF1\u7684\u7CBE\u795E\u8F6C\u79FB\u5230 JavaScript \u4E16\u754C\u4E2D\uFF0C\u5E76\u7AD9\u5728\u6211\u65C1\u8FB9\u3002\u6211\u4EEC\u5C06\u89C2\u5BDF\u6211\u4EEC\u7684\u5B87\u5B99\u6CD5\u5219\uFF0C\u5E76\u50CF\u7269\u7406\u5B87\u5B99\u4E2D\u7684\u7269\u7406\u5B66\u5BB6\u4E00\u6837\u8FDB\u884C\u5B9E\u9A8C\u3002</p><p><strong>\u6211\u4EEC\u5C06\u4E86\u89E3\u6211\u4EEC\u7684 JavaScript \u4E16\u754C\uFF0C\u800C\u65E0\u9700\u8003\u8651\u5B83\u662F\u5982\u4F55\u5B9E\u73B0\u7684\u3002\u8FD9\u7C7B\u4F3C\u4E8E\u7269\u7406\u5B66\u5BB6\u8BA8\u8BBA\u6052\u661F\u7684\u5C5E\u6027\u800C\u4E0D\u8D28\u7591*\u7269\u7406*\u4E16\u754C\u662F\u5426\u771F\u5B9E\u3002\u6CA1\u5173\u7CFB\uFF01\u65E0\u8BBA\u6211\u4EEC\u662F\u5728\u7814\u7A76\u7269\u7406\u4E16\u754C\u8FD8\u662F JavaScript \u4E16\u754C\uFF0C\u6211\u4EEC\u90FD\u53EF\u4EE5\u7528\u5B83\u4EEC\u81EA\u5DF1\u7684\u672F\u8BED\u6765\u63CF\u8FF0\u5B83\u4EEC\u3002</strong></p><p>\u6211\u4EEC\u7684\u5FC3\u667A\u6A21\u578B\u4E0D\u4F1A\u8BD5\u56FE\u89E3\u91CA\u4E00\u4E2A\u503C\u662F\u5982\u4F55\u5728\u8BA1\u7B97\u673A\u7684\u5185\u5B58\u4E2D\u8868\u793A\u7684\u3002\u7B54\u6848\u4E00\u76F4\u5728\u53D8\u5316\uFF01<a href="https://v8.dev/blog/react-cliff" target="_blank" rel="noopener noreferrer">\u5F53\u60A8\u7684\u7A0B\u5E8F\u8FD0\u884C\u65F6</a>\uFF0C\u7B54\u6848\u751A\u81F3\u4F1A\u53D1\u751F\u53D8\u5316\u3002\u5982\u679C\u4F60\u542C\u8FC7\u5173\u4E8E JavaScript \u5982\u4F55\u201C\u771F\u6B63\u201D\u8868\u793A\u5185\u5B58\u4E2D\u7684\u6570\u5B57\u3001\u5B57\u7B26\u4E32\u6216\u5BF9\u8C61\u7684\u7B80\u5355\u89E3\u91CA\uFF0C\u90A3\u5F88\u53EF\u80FD\u662F\u9519\u8BEF\u7684\u3002</p><p>\u5BF9\u6211\u4EEC\u6765\u8BF4\uFF0C\u6BCF\u4E2A\u5B57\u7B26\u4E32\u90FD\u662F\u4E00\u4E2A\u503C\u2014\u2014\u4E0D\u662F\u201C\u6307\u9488\u201D\u6216\u201C\u5185\u5B58\u5730\u5740\u201D\u2014\u2014\u4E00\u4E2A<em>\u503C</em>\u3002<strong>\u5728\u6211\u4EEC\u7684\u5B87\u5B99\u4E2D\uFF0C\u4E00\u4E2A\u503C\u5C31\u8DB3\u591F\u4E86\u3002</strong> \u4E0D\u8981\u8BA9\u201C\u8BB0\u5FC6\u7EC6\u80DE\u201D\u548C\u5176\u4ED6\u4F4E\u7EA7\u9690\u55BB\u5206\u6563\u60A8\u6784\u5EFA JavaScript \u7684\u51C6\u786E\u9AD8\u7EA7\u5FC3\u667A\u6A21\u578B\u7684\u6CE8\u610F\u529B\u3002\u53CD\u6B63\u90FD\u662F\u4E4C\u9F9F\uFF01</p><p>\u5982\u679C\u60A8\u6765\u81EA\u8F83\u4F4E\u7EA7\u522B\u7684\u8BED\u8A00\uFF0C\u8BF7\u629B\u5F00\u60A8\u5BF9\u201C\u901A\u8FC7\u5F15\u7528\u4F20\u9012\u201D\u3001\u201C\u5728\u5806\u6808\u4E0A\u5206\u914D\u201D\u3001\u201C\u5728\u5199\u5165\u65F6\u590D\u5236\u201D\u7B49\u7684\u76F4\u89C9\u3002\u8FD9\u4E9B\u5173\u4E8E\u8BA1\u7B97\u673A\u5982\u4F55\u5DE5\u4F5C\u7684\u6A21\u578B\u901A\u5E38\u4F7F\u4EBA\u4EEC<em>\u66F4\u96BE\u4EE5</em>\u5BF9 JavaScript \u4E2D\u53EF\u4EE5\u53D1\u751F\u6216\u4E0D\u80FD\u53D1\u751F\u7684\u4E8B\u60C5\u5145\u6EE1\u4FE1\u5FC3\u3002\u6211\u4EEC\u5C06\u67E5\u770B\u4E00\u4E9B\u8F83\u4F4E\u7EA7\u522B\u7684\u7EC6\u8282\uFF0C\u4F46\u53EA\u5173\u6CE8<a href="https://www.joelonsoftware.com/2002/11/11/the-law-of-leaky-abstractions/" target="_blank" rel="noopener noreferrer">\u771F\u6B63\u91CD\u8981\u7684\u5730\u65B9</a>\u3002\u5B83\u4EEC\u53EF\u4EE5\u4F5C\u4E3A\u6211\u4EEC\u5FC3\u667A\u6A21\u578B\u7684<em>\u8865\u5145</em>\uFF0C\u800C\u4E0D\u662F\u5B83\u7684\u57FA\u7840\u3002</p><p><strong>\u6211\u4EEC\u5FC3\u667A\u6A21\u578B\u7684\u57FA\u7840\u662F\u4EF7\u503C\u89C2\u3002</strong> \u6BCF\u4E2A\u503C\u90FD\u5C5E\u4E8E\u4E00\u4E2A\u7C7B\u578B\u3002\u539F\u59CB\u503C\u662F\u4E0D\u53EF\u53D8\u7684\u3002\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528\u6211\u4EEC\u79F0\u4E4B\u4E3A\u53D8\u91CF\u7684\u201C\u8FDE\u7EBF\u201D\u6765\u6307\u5411\u503C\u3002\u8FD9\u4E2A\u57FA\u7840\u2014\u2014\u5BF9\u4EF7\u503C\u89C2\u7684\u7406\u89E3\u2014\u2014\u5C06\u5E2E\u52A9\u6211\u4EEC\u7EE7\u7EED\u5EFA\u7ACB\u6211\u4EEC\u7684\u5FC3\u667A\u6A21\u578B\u3002</p><p>\u81F3\u4E8E\u8FD9\u4E9B\u5947\u5F02\u7684\u666F\u8C61\uFF0C\u6211\u4E0D\u518D\u591A\u60F3\u4E86\u3002\u6211\u6709\u7535\u7EBF\u8981\u6307\u51FA\uFF0C\u6709\u95EE\u9898\u8981\u95EE\u3002\u6211\u6700\u597D\u5F97\u5230\u5B83\uFF01</p><p>\u5F53\u6211\u770B\u7740\u661F\u661F\u65F6\uFF0C\u5B83\u4EEC\u662F\u660E\u4EAE\u7684\u3002</p><p>\u6211\u7728\u773C\u65F6\u5B83\u4EEC\u8FD8\u5728\u5417\uFF1F</p><p>\u6211\u8038\u4E86\u8038\u80A9\u3002</p><p><em>\u201C\u5B9E\u65BD\u7EC6\u8282\u3002\u201D</em></p>',18),i=[n];function o(s,d,_,c,h,l){return t(),a("div",null,i)}var g=e(p,[["render",o]]);export{f as __pageData,g as default};
